mapper.insert:
insert into jd_category (c_id, c_name, c_href
  )
values (#{cId,jdbcType=INTEGER}, #{cName,jdbcType=VARCHAR}, #{cHref,jdbcType=VARCHAR}
  )

mapper.update:
