mapper.insert:
insert into admin_user (user_id, username, password, 
  email, sex, phone, 
  status, create_time, update_time, 
  last_time, avatar, describes
  )
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{lastTime,jdbcType=TIMESTAMP}, #{avatar,jdbcType=VARCHAR}, #{describe,jdbcType=VARCHAR}
  )

mapper.update:
update admin_user
set
last_time=#{param2}
where username = #{param1}

update admin_user
set avatar = #{param2}
where user_id = #{param1}

mapper.select:
SELECT u.*,r.role_name as roleName FROM admin_user u LEFT JOIN admin_user_role ur ON (u.user_id = ur.user_id) LEFT JOIN admin_role r ON r.role_id = ur.role_id
u.username like concat('%',#{username},'%')
and u.create_time >=#{startTime}
and u.create_time <=#{endTime}

select user_id, username, password, email, sex, phone, status, create_time, update_time,
last_time, avatar, describes,code
from jd_user


    username like concat('%',#{username},'%')


    and create_time >=#{startTime}


    and  create_time <=#{endTime}
